<?php defined('SYSPATH') or die('No direct access allowed.');/** * User profile */class Model_Profile extends ORM {	// Relationships	protected $_has_one = array(		'user' => array('model' => 'user'),	);	/**	 * Creates a new profile associated with the specified user	 *	 * @param object User object	 * @param array  Values to be inserted	 * @param array  List of expected values	 */	public function create_profile(Model_ACL_User $user, $values, $expected)	{		// Add the user id to the profile and list of expected values		$values['user_id'] = $user->id;		$expected[] = 'user_id';				// Create the profile record		return $this->values($values, $expected)->create();	}}