<?php defined('SYSPATH') or die('No direct script access allowed');class Policy_Edit_Own_Profile extends Policy {	const NOT_ALLOWED   = 1;	const NOT_LOGGED_IN = 2;		/**	 * Method to execute the policy	 * 	 * @param Model_ACL_User $user  the user account to run the policy on	 * @param array          $extra an array of extra parameters that this policy	 *                              can use	 *	 * @return bool/int	 */	public function execute(Model_ACL_User $user, array $array = NULL)	{		if ( ! Auth::instance()->logged_in())			return self::NOT_LOGGED_IN;				$role_required = Kohana::$config->load('acl.edit_profile_role');		if ($user->is_a($role_required))		{			return TRUE;		}				return self::NOT_ALLOWED;	}}