<?php defined('SYSPATH') or die('No direct access allowed.');class Policy_Event_Add extends Policy {	const NOT_ALLOWED = 1;		public function execute(Model_ACL_User $user, array $extras = NULL)	{		// Admins, guild leadership, and event coordinators can all create events		if ($user->is_a('coordinator') OR $user->is_a('leadership') OR $user->is_an('admin'))		{			return TRUE;		}		else		{			return self::NOT_ALLOWED;		}	}}