<?php defined('SYSPATH') or die('No direct access allowed.');class View_Page_User_Login extends Abstract_View_Page {	public $title = 'Login';	public function action()	{		return '/'.Route::get('default')			->uri(array(				'controller' => 'user',				'action'     => 'login',			));	}		public function lostpw_url()	{		return '/'.Route::get('user')			->uri(array(				'action'     => 'lostpw',			));	}		public function lostname_url()	{		return '/'.Route::get('user')			->uri(array(				'action'     => 'lostname',			));	}		public function retrieval()	{		// Set as class property for later usage in retrieval_links()		$this->config = Kohana::$config->load('lost_data');				return $this->config->get('email_lost_password') OR $this->config->get('email_lost_username');	}		public function retrieval_links()	{		$links = array();				if ($this->config->get('email_lost_password'))		{			$links[] = array(				'url'  => $this->lostpw_url(),				'text' => 'Forgot your password?',			);		}				if ($this->config->get('email_lost_username'))		{			$links[] = array(				'url'  => $this->lostname_url(),				'text' => 'Forgot your username?',			);		}				return $links;	}}